import React, { Component } from 'react';
import ResponsiveContainer from '../Navs/Header';
import Footer from '../Navs/Footer';
import { Segment, Menu, Tab, /* Grid */ } from 'semantic-ui-react';
// import { Link } from 'react-router-dom'
// import * as ROUTES from '../constants/routes'
import { PasswordChangeForm } from '../PasswordChange';
import { PasswordForgetForm } from '../PasswordForget';

class UserSettings extends Component {
    state = { activeItem: 'pw_change' }

    handleItemClick = (e, { name }) => this.setState({ activeItem: name })
    render() {
        // const { activeItem } = this.state
        const panes = [
            {
                menuItem: 'Change Password', render: () => <Tab.Pane key="Tab 1">
                    <PasswordChangeForm />
                </Tab.Pane>
            },
            {
                menuItem: 'Reset Password', render: () =>
                <Tab.Pane>
                    <PasswordForgetForm />
                </Tab.Pane>
            }
        ]

        return (
            <div>
                <ResponsiveContainer>
                    <Segment vertical stacked style={{ minHeight: 700 }}>
                        <Tab menu={{ fluid: true, vertical: true, tabular: true }} panes>


                        </Tab>
                        {/* <Menu pointing secondary vertical as="segment">
                            <Menu.Item name='pw_change' active={activeItem === 'pw_change'} onClick={this.handleItemClick}>
                                Change Password
                            </Menu.Item>
                            <Menu.Item name='pw_forget' active={activeItem === 'pw_forget'} onClick={this.handleItemClick}>
                                Reset Password
                            </Menu.Item>
                        </Menu> */}




                    </Segment>
                    <Footer />
                </ResponsiveContainer>
            </div>
        );
    }
}

export default UserSettings;
