import React, { Component } from 'react'
import { Button } from 'semantic-ui-react';
import { withFirebase } from '../Firebase';

const UserButton = ({ firebase }) => (
    <button type="button">
        {email}
    </button>
)

class UserEmail extends Component {
    constructor(props) {
        super(props)
        this.state = {
            email: null
        }
    }

    componentWillMount() {
        const email = this.props.firebase.displayUserEmail()
        this.setState({
            email
        })
    }

    render() {
        const { email } = this.state
        return (
            <p>{email}</p>
        )
    }
}

export default withFirebase(UserButton)
