import React, { Component } from 'react'
import { Button } from 'semantic-ui-react';
import { withFirebase } from '../Firebase';

const AccountButton = () => (
    // <button type="button">
    <Email />
    // </button>
)

class UserEmail extends Component {
    constructor(props) {
        super(props)
        this.state = {
            email: null
        }
    }

    componentWillMount() {
        try {
            const email = this.props.firebase.displayUserEmail()

            this.setState({
                email
            })
        } catch (error) { }
    }

    componentDidMount() {
        try {
            const email = this.props.firebase.displayUserEmail()

            this.setState({
                email
            })
        } catch (error) { }
    }

    componentDidUpdate

    render() {
        const { email } = this.state
        return (
            <div>
                {email === null && <Button>Sign In</Button>}
                {email && <Button>{email}</Button>}
            </div>
        )
    }
}

const Email = withFirebase(UserEmail)

export default withFirebase(AccountButton)
