import React, { Component } from 'react'
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react'
import { Link, withRouter } from "react-router-dom"
import { compose } from 'recompose';

import * as ROUTES from '../constants/routes';
import { withFirebase } from '../Firebase';

const INITIAL_STATE = {
    username: '',
    email: '',
    passwordOne: '',
    passwordTwo: '',
    error: null,
};

const SignUpPage = () => (
    <div>
        <Grid textAlign="center" className="login-background" style={{
            height: 100 + 'vh',
            // background: "linear-gradient(300deg, rgba(210,142,142,1) 21%, rgba(203,17,17,1) 60%, rgba(247,63,63,1) 100%)",
            margin: 0
        }} verticalAlign="middle">
            <Grid.Column
                floated="right"
                style={{
                    paddingTop: "20vh",
                    maxWidth: 450,
                    backgroundColor: "white",
                    height: "100vh"
                }}
            >
                <Header as="h2" style={{ color: "black" }} textAlign="center">
                    <Image src={require('../assets/images/album-icon.png')} circular />
                    Sign Up to Project Album
                </Header>

                <SignUpForm />

                <Message>
                    Already have an account? <Link to={ROUTES.SIGN_IN}>Log in</Link>
                </Message>
            </Grid.Column>
        </Grid>
    </div>
)

class SignUpFormBase extends Component {
    constructor(props) {
        super(props);

        this.state = { ...INITIAL_STATE }
    }

    onSubmit = (event) => {
        const { username, email, passwordOne } = this.state

        this.props.firebase
            .createUserWithEmailAndPassword(email, passwordOne)
            .then(this.props.firebase.c)
            .catch(error => {
                this.setState({ error })
            })

        event.preventDefault();
    }

    onChange = (event) => {
        this.setState({ [event.target.name]: event.target.value })
    }

    render() {

        const {
            username,
            email,
            passwordOne,
            passwordTwo,
            error,
        } = this.state;

        const isInvalid =
            passwordOne !== passwordTwo ||
            passwordOne === '' ||
            passwordOne.length < 8 ||
            email === '' ||
            username === '';

        return (
            <div>
                <Form size="large" onSubmit={this.onSubmit}>
                    <Segment stacked >

                        <Form.Input
                            fluid
                            name="username"
                            value={username}
                            icon="address card"
                            iconPosition="left"
                            placeholder="Full Name"
                            onChange={this.onChange}
                        />
                        <Form.Input
                            fluid
                            name="email"
                            value={email}
                            icon="user"
                            iconPosition="left"
                            placeholder="E-mail Address"
                            onChange={this.onChange}
                        />
                        <Form.Input
                            fluid
                            name="passwordOne"
                            value={passwordOne}
                            icon='lock'
                            iconPosition="left"
                            placeholder="Password"
                            type="password"
                            onChange={this.onChange}
                        />
                        <Form.Input
                            fluid
                            name="passwordTwo"
                            value={passwordTwo}
                            icon='lock'
                            iconPosition='left'
                            placeholder="Confirm Password"
                            type="password"
                            onChange={this.onChange}
                        />

                        {/* <Link to={ROUTES.ACCOUNT} style={{ color: "black" }}> */}
                        <Button disabled={isInvalid} color="green" fluid size="large" type='submit'>
                            Sign up
                        </Button>

                        {error && <p>{error.message}</p>}
                        {/* </Link> */}
                    </Segment>
                </Form>

            </div>
        )
    }
}

const SignUpLink = () => (
    <p>
        Don't have an account? <Link to={ROUTES.SIGN_UP}>Sign up</Link>
    </p>
)

const SignUpForm = compose(
    withRouter,
    withFirebase
)(SignUpFormBase)

export default SignUpPage
export { SignUpLink, SignUpForm }
