import React, { Component } from 'react';
import { Menu } from 'semantic-ui-react';
import { Link } from 'react-router-dom'
import { AuthUserContext } from '../Session';
import * as ROUTES from '../constants/routes'

class SidebarAccountBase extends Component {
    render() {
        return (
            <div>
                <AuthUserContext.Consumer>
                    {
                        authUser => (
                            authUser
                                ? <Menu.Item>
                                    {authUser.email}

                                    <Menu.Menu>
                                        <Menu.Item as={Link} to={ROUTES.ACCOUNT} style={{ fontSize: 14, paddingLeft: 30 }}>
                                            Account
                                        </Menu.Item>

                                        <Menu.Item as={Link} to={ROUTES.SETTINGS} style={{ fontSize: 14, paddingLeft: 30 }}>
                                            Settings
                                        </Menu.Item>

                                        <Menu.Item
                                            onClick={() => {
                                                this.props.firebase.signOut()
                                            }}
                                            to={ROUTES.ACCOUNT}
                                            style={{ fontSize: 14, paddingLeft: 30 }}
                                        >
                                            Sign Out
                                        </Menu.Item>
                                    </Menu.Menu>
                                </Menu.Item>

                                : <Menu.Item as={Link} to={ROUTES.SIGN_IN}>
                                    Sign In
                                    </Menu.Item>
                        )

                    }
                </AuthUserContext.Consumer>
            </div>
        );
    }
}

export default SidebarAccount;
