import React from 'react'
import { Route, Redirect } from 'react-router-dom'
import * as ROUTES from './routes'
// import auth0Client from './Auth';
import { Loader } from 'semantic-ui-react';

const SecuredRoute = (component, path, checkingSession, authUser) => (
    <Route path={path}>
        {(checkingSession) && <Loader active size="big" content="Loading Session..." />}
        {

            // auth0Client.signIn()
            <div><Redirect to={ROUTES.SIGN_IN} /></div>
        }
        {this.component}
    </Route>
)


export default SecuredRoute
