import React from 'react'
import { Route, Redirect } from 'react-router-dom'
import * as ROUTES from './routes'
// import auth0Client from './Auth';
import { Loader } from 'semantic-ui-react';

const SecuredRoute = Component => {
    class SecuredRout extends React.Component {
        render(){
    const { component: Component, path, checkingSession, authUser } = props;
    return (
        <Route path={path} render={() => {
            if (checkingSession) return <Loader active size="big" content="Loading Session..." />
            if (authUser === null) {

                // auth0Client.signIn()
                return <div><Redirect to={ROUTES.SIGN_IN} /></div>
            }
            return <Component />
        }} />
    )}
}
}

export default SecuredRoute
