import React, { Component } from 'react'
import { Button } from 'semantic-ui-react';
import { withFirebase } from '../Firebase';
import { Link, withRouter } from "react-router-dom"
import * as ROUTES from '../constants/routes';
import { compose } from 'recompose';

const AccountButton = () => (
    // <button type="button">
    <Email />
    // </button>
)

class UserEmail extends Component {
    constructor(props) {
        super(props)
        this.state = {
        }
    }

    /* componentWillMount() {
        try {
            const email = this.props.firebase.displayUserEmail()

            this.setState({
                email
            })
        } catch (error) { }
    } */

    componentDidMount() {
        try {
            const email = this.props.firebase.displayUserEmail()

            this.setState({
                email
            })
        } catch (error) {
            this.setState({
                email: null
            })
        }
        this.forceUpdate()
    }

    /* componentDidUpdate() {
        try {
            const email = this.props.firebase.displayUserEmail()

            this.setState({
                email
            })
        } catch (error) { }
    } */

    render() {
        const { email } = this.state
        return (
            <div>
                {email === null &&
                    <Link>
                        <Button inverted>Sign In</Button></Link>}
                {email && <Button>{email}</Button>}
            </div>
        )
    }
}

const Email = compose(withRouter, withFirebase)(UserEmail)

export default withFirebase(AccountButton)
